import { setUserDetails } from '../redux/UserRedux/userAction';
import store from '../redux/store';
import { useSnackbar } from 'notistack';
import axios from 'axios';

export const AddtoCartHelper = (id:any, storeData:any) => {
    return new Promise((res,rej) => {
        axios.patch("http://localhost:5000/user/addtocart", 
            {
                "widget": {
                    "id": id,
                    "subs_type": "hits",
                    "hits_count": 4000,
                    "days_count": 0,
                    "price": 3000
                },
                "username": storeData.user.userDetails.username
            }
        )
        .then((x:any) => {
            console.log("added", x.data)
            res(x.data);
        })
    })
};

export const getAllWidgets = () => {
    return new Promise((resolve:any,rej:any) => {
        axios.get("http://localhost:5000/products")
        .then(x => {
            resolve(x.data);
        });
    })
}

