import { createReducer } from "@reduxjs/toolkit";
import { setCartWidgets, setLoginStatus, setPurchasedWidgets, setUserData, setUserDetails, setUsername } from "./userAction";

const initialState = {
    isLoggedIn: false,
    username: "gaurav",
    userDetails: {
        firstname: "",
        lastname: ""
    },
    purchasedWidgets: [],
    cartWidgets: [],
    loginErrMsg: ""
}

export const userReducer = createReducer(initialState, (builder) => {

    builder.addCase(setLoginStatus, (state, action) => ({...state, isLoggedIn : action.payload}));

    builder.addCase(setUsername, (state, action) => ({...state, username : action.payload}));

    builder.addCase(setCartWidgets, (state, action) => ({...state, cartWidgets : action.payload}));

    builder.addCase(setPurchasedWidgets, (state, action) => ({...state, purchasedWidgets : action.payload}));

    builder.addCase(setUserDetails, (state, action) => ({...state, userDetails : action.payload}));

});

