import React, { Dispatch, useEffect, useState } from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import { useSelector } from 'react-redux';
import "./CustomPopup.scss";
import { IconButton, Select, SelectChangeEvent } from '@mui/material';
import { MenuItem } from '@mui/material';
import { ListSubheader } from '@mui/material';
import { InputLabel } from '@mui/material';
import { FormControl } from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import { subsStatement } from '../../Utils/cartFunctions';

const style = {
  position: 'absolute' as 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 1,
};

type CustomPopupProps = {
  widgetObj: any;
  setShowATCPopup: any;
};

// const defaultProps: CustomPopupProps = {
//   widgetObj: {}
//   setShowATCPopup: 
// };

export default function CustomPopup({widgetObj, setShowATCPopup}: CustomPopupProps) {
  const [open, setOpen] = React.useState(true);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const storeData = useSelector((data:any) => data.popup.popupData);
  const [subsType, setSubsType] = React.useState("Hits");
  const [wtDetails, setWtDetails] = React.useState({});
  const [wtCount, setWtCount] = React.useState<any>(0);
  const [wtPrice, setWtPrice] = React.useState<any>(0);

  const [age, setAge] = React.useState<any>(10);

  const handleChange = (event: SelectChangeEvent) => {
    setAge(event.target.value);
  };

  useEffect(() => {
    CalcPrice()
  }, [wtCount, subsType])

  console.log("cc", widgetObj)

  const cartObj = {
    "details" : wtDetails,
    "widget" : widgetObj
  }

  const CalcPrice = () => {
    if(subsType == "Hits"){
      setWtPrice((wtCount)/10);
    }
    else{
      setWtPrice((wtCount)*5);
    }
  }

  return (
    <div className='popup_container'>
        <div className='popup_box'>
            <div className='popup_header'>
                <div className='header_text'>{storeData.heading}</div> 
                <div className='closebutton'>
                  <IconButton onClick={() => setShowATCPopup(false)}>
                    <CloseIcon/>
                  </IconButton>
                </div>
            </div>
            <div className='popup_body'>
              <FormControl sx={{ m: 1, minWidth: "93%" }} size="small">
                <InputLabel id="demo-select-small">Type</InputLabel>
                <Select
                    labelId="demo-select-small"
                    id="demo-select-small"
                    defaultValue={subsType}
                    value={subsType}
                    label="Type"
                    onChange={(e) => {setSubsType(e.target.value)}}
                    MenuProps={{
                        style: {zIndex: 35001},
                        disableScrollLock: true,
                    }}
                    autoWidth
                >
                    <MenuItem value="">
                    <em>Select Type</em>
                    </MenuItem>
                    <MenuItem value={"Days"}>Number of Days</MenuItem>
                    <MenuItem value={"Hits"}>Number of Hits</MenuItem>
                </Select>
              </FormControl>

              <div className='popup_row'>
                <div className='input_container'>
                  <input type="text" placeholder='#' defaultValue={wtCount} value={wtCount} onChange={(e) => {setWtCount(e.target.value);}}/> {subsType}
                </div>
                <span className='pp_price'>â‚¹ {wtPrice} </span>
              </div>
              <div className='popup_row'>
                  <span className='pp_subStatement'>{subsStatement(widgetObj)}</span>
                  <button className='popup_ATC'>Add to Cart</button>
              </div>

            </div>
        </div>
    </div>
  );
}
