import { setisLoggedIn, setLoginErrMsg, setUserDetails } from '../redux/UserRedux/userAction';
import store from '../redux/store';
import axios from 'axios';

export const setReduxUser = (username:any) => {

    axios.get("http://localhost:5000/user/" + username)
    .then((user:any) => {
        let data = user.data;
        console.log(data);

        let details = {
            username: "gaurav",
            firstname: "",
            lastname: "",
        }

    })

    // store.dispatch(setUserDetails(details));
}

export const ValidateUser = (uname:any, pswd:any) => {
    return new Promise((res,rej) => {
        let userObj = {
            "username": uname,
            "password": pswd
        }
        axios.post("http://localhost:5000/user/login", userObj)
        .then((x:any) => {
        let isAuth = x.data.authenticated;
        if(!isAuth){
            store.dispatch(setLoginErrMsg(x.data.errorMsg));
            localStorage.setItem("auth", "false");
        }
        else{
            store.dispatch(setisLoggedIn(true));
            store.dispatch(setLoginErrMsg("Authenticated !"));
            window.location.href = "/";
            localStorage.setItem("auth", "true");
        }
        });
    })

  }

export const logOutUser = () => {
    store.dispatch(setisLoggedIn(false));
    window.location.href = "login";
    localStorage.setItem("auth", "false");
};
