import { setLoginErrMsg, setLoginStatus, setUserDetails } from '../redux/UserRedux/userAction';
import store from '../redux/store';
import axios from 'axios';

export const setUser = (username:any, password:any) => {
    let obj = {
        "username": username,
        "password": password
    }
    store.dispatch(setUserDetails(obj));
}

// export const loginUser = () => {
//     return new Promise((res,rej) => {
//         store.dispatch(setLoginStatus(true));
//         localStorage.setItem("auth", "true");
//     })
// };

export const ValidateUser = (uname:any, pswd:any) => {
    return new Promise((res,rej) => {
        let body = {
            "username": uname,
            "password": pswd
          }
        axios.post("http://localhost:5000/user/login", body)
        .then((x:any) => {
        let isAuth = x.data.authenticated;
        if(!isAuth){
            store.dispatch(setLoginErrMsg(x.data.errorMsg));
            localStorage.setItem("auth", "false");
        }
        else{
            store.dispatch(setLoginStatus(true));
            store.dispatch(setLoginErrMsg("Authenticated !"));
            window.location.href = "/";
            localStorage.setItem("auth", "true");
        }
        });
    })

  }

export const logOutUser = () => {
    store.dispatch(setLoginStatus(false));
    window.location.href = "login";
    localStorage.setItem("auth", "false");
};
