import React, { Dispatch, useEffect, useState } from 'react';
import "./CardsContainer.scss";
import axios from 'axios';
import Button from '@mui/material/Button';
import { useDispatch, useSelector } from 'react-redux';
import { SnackbarProvider, VariantType, useSnackbar } from 'notistack';
import { loginUser } from '../../Utils/userFunctions';
import { setLoginStatus } from '../../redux/UserRedux/userAction';
import { AddtoCartHelper } from '../../Utils/cartFunctions';

function CardsContainer() {

    const [cardsArr, setCardsArr] = useState([]);
    const { enqueueSnackbar } = useSnackbar();
    const storeData = useSelector((data:any) => data);
    const dispatch = useDispatch();

    useEffect(() => {
        getAllProducts();
    }, []);
    
    const getAllProducts = () => { 
        axios.get("http://localhost:5000/products")
        .then(x => setCardsArr(x.data));
    }

    // const addToCart = (id:any) => {
    //     axios.post("http://localhost:5000/cart", 
    //         {
    //             "id": id,
    //             "subs_type": "hits",
    //             "hits_count": 4000,
    //             "days_count": 0,
    //             "price": 3000
    //         }
    //     )
    //     .then(x => {
    //         console.log("added")
    //         // showSnackBar("Item added to cart", 'success');
    //         enqueueSnackbar(`Item added to cart.`, { variant: "success" });
    //     })
    // }

    const addToCart = (id:any, dispatch?:any) => {
        axios.patch("http://localhost:5000/user/addtocart", 
            {
                "widget": {
                    "id": id,
                    "subs_type": "hits",
                    "hits_count": 4000,
                    "days_count": 0,
                    "price": 3000
                },
                "username": "gaurav"
            }
        )
        .then(x => {
            console.log("added", x.data)
            enqueueSnackbar(`Item added to cart.`, { variant: "success" });
        })
    }

    function disp(id:any){
        console.log(storeData.user)
        AddtoCartHelper(id, storeData)
        .then(x => {
            enqueueSnackbar(`Item added to cart.`, { variant: "success" });
        })
    }

    return (
        <>
            <button onClick={() => {loginUser(); console.log(storeData.user)}}>CLICK</button>
            <div className='card_container'>
                {cardsArr.map((card:any) => (
                    <div className='card'>
                        <div className='card_img'></div>
                        <div className='card_title'>{card.title}</div>
                        <div className='card_footer'>
                            {/* <div className='hits_count'></div> */}
                            {/* <div className='card_price'>â‚¹ 200</div> */}
                            <button className='viewcode_button'>{"</>"}</button>
                            {/* <button className='add_cart_button' onClick={() => addToCart(card.id)}>Add to Cart</button> */}
                            <button className='add_cart_button' onClick={() => disp(card.id)}>Add to Cart</button>
                        </div>
                    </div>
                ))}
            </div>
        </>
    )
}

export default CardsContainer
