import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import "../CartPage/CartPage.scss";

function CartPage() {

    const [widgitArr, setWidgitArr] = useState([]);

    const storeData = useSelector((data:any) => data.popup);

    useEffect(() => {
        getAllProducts();
        // getOneWidget(1)
        console.log(storeData)
    }, []);

    const getOneWidget = async (id:any) => {
        axios.get("http://localhost:5000/products/"+id)
        .then(x => {
            console.log(x.data);
            return x.data;
        });
    }

    const getAllProducts = async () => { 
        let arr:any = [];
        axios.get("http://localhost:5000/cart")
        .then(x => {
            x.data.map((x:any) => {
                const a = getOneWidget(x.id);
                arr.push(a);
            });
            console.log(arr)
            setWidgitArr(arr);
        });
    }



  return (
    <div className='CartPage_container'>
        <div className='selected_widgets'>
            <div className='cart_heading'>Cart</div>
            {widgitArr.map((widgit:any) => (
                <div className='cart_widgetbox'>
                    <div className='cart_widget_img'></div>
                    <div className='cart_widget_details'>
                        <div className='cart_widget_name'>{widgit.title}</div>
                        <div className='cart_widget_desc'>{widgit.imgUrl}</div>
                        <div className='cart_widget_amount'>
                            <div className='cart_widget_systype'>Subscription for 1000 hits</div>
                            <div className='cart_widget_buttons'>Edit | Remove</div>
                        </div>
                    </div>
                </div>
            ))}
        </div>
        <div className='amount_box'>
            <div className='cart_heading'>Price</div>
            <div className='price_row'>
                <span>Item Price</span>
                <span>₹ 200</span>
            </div>
            <div className='price_row'>
                <span>Coupon Discount</span>
                <span>₹ 0</span>
            </div>
            <div className='total_price price_row'>
                <span>Total</span>
                <span>₹ 200</span>
            </div>
        </div>
    </div>
  )
}

export default CartPage;
