import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import "../CartPage/CartPage.scss";
import CircularProgress from '@mui/material/CircularProgress';

function CartPage() {
    const [allWidgets, setAllWidgets] = useState([]);
    const [allCartWidgets, setAllCartWidgets] = useState([]);
    const [widgitArr, setWidgitArr] = useState([]);
    const storeData = useSelector((data:any) => data.popup);
    const [showLoader, setShowLoader] = useState(false);

    useEffect(() => {
        // getAllWidgets();
        // getCartWidgets();
        setShowLoader(true);
        populateCart();
        // getOneWidget(1)
    }, []);

    const getOneWidget = (id:any) => {
        return new Promise((resolve:any,rej:any) => {
            axios.get("http://localhost:5000/products/"+id)
            .then(x => {
                resolve(x.data);
            });
        })
    }

    const getAllWidgets = () => {
        return new Promise((resolve:any,rej:any) => {
            axios.get("http://localhost:5000/products")
            .then(x => {
                setAllWidgets(x.data);
                resolve(x.data);
            });
        })
    }

    const getCartWidgets = () => { 
        return new Promise((resolve:any,rej:any) => {
            axios.get("http://localhost:5000/cart")
            .then(x => {
                setAllCartWidgets(x.data);
                resolve(x.data)
            });
        })
    }

    const populateCart = () => {
        let arr:any = [];
        getCartWidgets().then((cw:any) => {
            cw.map((wid:any) => {
                getAllWidgets().then((aw:any) => {
                    let obj = aw.filter((x:any) => x.id == wid.id)[0];
                    arr.push(obj);
                    setShowLoader(false);
                });
                // getOneWidget(wid.id).then((z:any) => {
                //     console.log(z)
                //     arr.push(z);
                // })
            })
        })
        console.log(arr)
        setWidgitArr(arr);
    }

    // const populateCart = async () => {
    //     const ab = await getAllWidgets();
    //     const cd = await getCartWidgets();
    //     let arr:any = [];
    //     console.log(cd)
    //     allCartWidgets.map((wid:any) => {
    //         let obj = allWidgets.filter((x:any) => x.id == wid.id)[0];
    //         arr.push(obj);
    //     })
    //     setWidgitArr(arr);
    // }

    return (
        <div className='CartPage_container'>
            <div className='selected_widgets'>
                <div className='cart_heading' onClick={() => populateCart()}>Cart</div>
                {showLoader ? 
                    (<>
                        <div className='cart_loader'>
                            <CircularProgress/>
                        </div>
                    </>) 
                    : 
                    (widgitArr.map((widgit:any) => (
                        <div className='cart_widgetbox'>
                            <div className='cart_widget_img'></div>
                            <div className='cart_widget_details'>
                                <div className='cart_widget_name'>{widgit.title}</div>
                                <div className='cart_widget_desc'>{widgit.imgUrl}</div>
                                <div className='cart_widget_amount'>
                                    <div className='cart_widget_systype'>Subscription for 1000 hits</div>
                                    <div className='cart_widget_buttons'>Edit | Remove</div>
                                </div>
                            </div>
                        </div>
                    )))
                }
            </div>
            <div className='amount_box'>
                <div className='cart_heading'>Price</div>
                <div className='price_row'>
                    <span>Item Price</span>
                    <span>₹ 200</span>
                </div>
                <div className='price_row'>
                    <span>Coupon Discount</span>
                    <span>₹ 0</span>
                </div>
                <div className='total_price price_row'>
                    <span>Total</span>
                    <span>₹ 200</span>
                </div>
            </div>
        </div>
    )
}

export default CartPage;
