import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import "../CartPage/CartPage.scss";
import CircularProgress from '@mui/material/CircularProgress';
import ProductionQuantityLimitsIcon from '@mui/icons-material/ProductionQuantityLimits';
import { setReduxUser } from '../../Utils/userFunctions';

function CartPage() {
    const [allWidgets, setAllWidgets] = useState([]);
    const [allCartWidgets, setAllCartWidgets] = useState([]);
    const [widgitArr, setWidgitArr] = useState([]);
    const storeData = useSelector((data:any) => data);
    const [showLoader, setShowLoader] = useState(false);
    const [CartEmptyMsg, setCartEmptyMsg] = useState("Cart is empty !");

    useEffect(() => {
        // getAllWidgets();
        // getCartWidgets();
        setShowLoader(true);
        // populateCart();
        // setReduxUser(storeData?.user?.userDetails?.username)
        populateCart2();
        // getOneWidget(1)
    }, []);

    useEffect(() => {
        populateCart2()
    },[storeData])

    const getOneWidget = (id:any) => {
        return new Promise((resolve:any,rej:any) => {
            axios.get("http://localhost:5000/products/"+id)
            .then(x => {
                resolve(x.data);
            });
        })
    }

    const getAllWidgets = () => {
        return new Promise((resolve:any,rej:any) => {
            axios.get("http://localhost:5000/products")
            .then(x => {
                setAllWidgets(x.data);
                resolve(x.data);
            });
        })
    }

    const populateCart2 = () => {
        setShowLoader(true);
        let arr:any = [];
        let dbArr = storeData?.user?.userDetails?.cartWidgets;
        if(dbArr?.length > 0){
            dbArr.map((wid:any) => {
                getOneWidget(wid.id)
                .then((aw:any) => {
                    // let obj = aw.filter((x:any) => x.id == wid.id)[0];
                    arr.push(aw);
                });
            })
            setWidgitArr(arr);
            setShowLoader(false);
        }
    }

    const dataReturn = (sData:any, index:any) => {
        let arr:any = [];
        if(sData?.length > 0){
            sData.map((wid:any) => {
                getOneWidget(wid.id)
                .then((aw:any) => {
                    arr.push(aw);
                });
            })
        }
        return arr[index]
    }

    // const getCartWidgets = () => { 
    //     return new Promise((resolve:any,rej:any) => {
    //         axios.get("http://localhost:5000/cart")
    //         .then(x => {
    //             setAllCartWidgets(x.data);
    //             resolve(x.data)
    //         });
    //     })
    // }

    const getCartWidgets = () => { 
        return new Promise((resolve:any,rej:any) => {
            axios.get("http://localhost:5000/user/gaurav")
            .then(x => {
                setAllCartWidgets(x.data.cartwidgets);
                resolve(x.data.cartwidgets)
            });
        })
    }

    // const populateCart = () => {
    //     setShowLoader(true);
    //     let arr:any = [];
    //     getCartWidgets().then((cw:any) => {
    //         if(cw.length > 0){
    //             cw.map((wid:any) => {
    //                 getAllWidgets().then((aw:any) => {
    //                     let obj = aw.filter((x:any) => x.id == wid.id)[0];
    //                     arr.push(obj);
    //                     setShowLoader(false);
    //                 });
    //             })
    //         }
    //         else{
    //             setCartEmptyMsg("Cart is empty !")
    //             setShowLoader(false);
    //         }
    //     })
    //     console.log(arr)
    //     setWidgitArr(arr);
    // }



    // const deleteCartItem = (id:any) => {
    //     axios.delete("http://localhost:5000/cart/" + id)
    //     .then(x => {
    //         console.log("Deleted id = ", id);
    //         populateCart2();
    //     })
    // }

    const deleteCartItem = (id:any, username:any) => {
        let body:any = {
            "widgetId": id,
            "username": username
        }
        axios.patch("http://localhost:5000/user/deletefromcart", body)
        .then(x => {
            console.log("Deleted id = ", id);
            setReduxUser(storeData?.user?.userDetails?.username).then(y => populateCart2())
            
        })
    }

    // const populateCart = async () => {
    //     const ab = await getAllWidgets();
    //     const cd = await getCartWidgets();
    //     let arr:any = [];
    //     console.log(cd)
    //     allCartWidgets.map((wid:any) => {
    //         let obj = allWidgets.filter((x:any) => x.id == wid.id)[0];
    //         arr.push(obj);
    //     })
    //     setWidgitArr(arr);
    // }

    return (
        <div className='CartPage_container'>
            <div className='selected_widgets'>
            <button onClick={() => {setReduxUser(storeData.user.userDetails.username)}}>Console Redux</button>
                <div className='cart_heading' onClick={() => populateCart2()}>Cart</div>
                {showLoader ? 
                    (<>
                        <div className='cart_loader'>
                            <CircularProgress/>
                        </div>
                    </>) 
                    : 
                    (storeData?.user?.userDetails?.cartWidgets.length > 0 ?
                        storeData?.user?.userDetails?.cartWidgets.map((widgit:any) => (
                        <div className='cart_widgetbox'>
                            <div className='cart_widget_img'></div>
                            <div className='cart_widget_details'>
                                <div className='cart_widget_name'>{dataReturn(storeData?.user?.userDetails?.cartWidgets, 0)?.title}</div>
                                <div className='cart_widget_desc'>{widgit?.imgUrl}</div>
                                <div className='cart_widget_amount'>
                                    <div className='cart_widget_systype'>Subscription for 1000 hits</div>
                                    <div className='cart_widget_buttons'>Edit | <span className='removebtn' onClick={() => deleteCartItem(widgit?.id, storeData?.user?.userDetails?.username)}>Remove</span></div>
                                </div>
                            </div>
                        </div>
                        
                    ))
                    :
                    <div className='EmptyCart'> <ProductionQuantityLimitsIcon/> <span>{CartEmptyMsg}</span></div>
                    )
                }
            </div>
            <div className='amount_box'>
                <div className='cart_heading'>Price</div>
                <div className='price_row'>
                    <span>Item Price</span>
                    <span>₹ 200</span>
                </div>
                <div className='price_row'>
                    <span>Coupon Discount</span>
                    <span>₹ 0</span>
                </div>
                <div className='total_price price_row'>
                    <span>Total</span>
                    <span>₹ 200</span>
                </div>
                <button className='placeorder_btn'>Place Order</button>
            </div>
        </div>
    )
}

export default CartPage;
