import { setCartWidgets, setLoginStatus } from '../redux/UserRedux/userAction';
import store from '../redux/store';
import { useSnackbar } from 'notistack';
import axios from 'axios';

const { enqueueSnackbar } = useSnackbar();

export const AddtoCartHelper = (id:any, subs_type:any, hits_count:any, days_count:any, price:any, storeData:any) => {

    axios.patch("http://localhost:5000/user/addtocart", 
        {
            "widget": {
                "id": id,
                "subs_type": subs_type,
                "hits_count": hits_count,
                "days_count": days_count,
                "price": price
            },
            "username": storeData.user.username
        }
    )
    .then((x:any) => {
        console.log("added", x.data)
        store.dispatch(setCartWidgets(x.data));
        enqueueSnackbar(`Item added to cart.`, { variant: "success" });
    })
};
