const initialState = {
    isLoggedIn: false,
    username: "",
    firstname: "",
    lastname: "",
    purchasedWidgets: [],
    cartWidgets: []
}

export const userReducer = (state = { userData: initialState }, action:any) => {
    switch (action.type) {
  
        case "LOGIN_SUCCESS":
            return { ...state, userData: [{...state.userData}, action.payload] };

        case "LOGOUT":
            return { userData: action.payload };

        case "SET_USERNAME":
            return { userData: action.payload };

        default:
            return state;
    }
};
