import * as React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import { useSelector } from 'react-redux';
import "./CustomPopup.scss";
import { IconButton, Select, SelectChangeEvent } from '@mui/material';
import { MenuItem } from '@mui/material';
import { ListSubheader } from '@mui/material';
import { InputLabel } from '@mui/material';
import { FormControl } from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import { subsStatement } from '../../Utils/cartFunctions';

const style = {
  position: 'absolute' as 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 1,
};

type CustomPopupProps = {
  widgetObj: object;
};

const defaultProps: CustomPopupProps = {
  widgetObj: {}
};

export default function CustomPopup({widgetObj}: CustomPopupProps) {
  const [open, setOpen] = React.useState(true);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const storeData = useSelector((data:any) => data.popup.popupData);

  const [age, setAge] = React.useState<any>(10);

  const handleChange = (event: SelectChangeEvent) => {
    setAge(event.target.value);
  };

  return (
    <div className='popup_container'>
        <div className='popup_box'>
            <div className='popup_header'>
                <div className='header_text'>{storeData.heading}</div> 
                <div className='closebutton'>
                  <IconButton>
                    <CloseIcon/>
                  </IconButton>
                </div>
            </div>
            <div className='popup_body'>
              <FormControl sx={{ m: 1, minWidth: "93%" }} size="small">
                <InputLabel id="demo-select-small">Type</InputLabel>
                <Select
                    labelId="demo-select-small"
                    id="demo-select-small"
                    value={age}
                    label="Age"
                    onChange={handleChange}
                    MenuProps={{
                        style: {zIndex: 35001, width: "100%"}
                    }}
                    autoWidth
                    // sx={width: "100%"}
                >
                    <MenuItem value="">
                    <em>Select Type</em>
                    </MenuItem>
                    <MenuItem value={10}>Number of Days</MenuItem>
                    <MenuItem value={20}>Number of Hits</MenuItem>
                </Select>
              </FormControl>

              <div className='popup_row'>
                <div className='input_container'>
                  <input type="text" placeholder='#'/> hits
                </div>
                <span>â‚¹ 2,000</span>
              </div>

              <div className='popup_row'>
                  <span className='pp_subStatement'>{subsStatement(widgetObj)}</span>
                  <button className='popup_ATC'>Add to Cart</button>
              </div>

            </div>
        </div>
    </div>
  );
}
